import ColorItem from "./ColorItem";
import { NavLink } from "react-router-dom";
import '../css/bottom.css'
import { useSelector, useDispatch } from "react-redux";
import { selectColor } from "../actions/SelectAction";


//bekommt Liste der zur Auswahl stehenden Farben
const ColorSelection = () => {

    const dispatch = useDispatch();
    const selectedColor = useSelector(state => state.config.color);
    const colors = ["Model1", "Model2", "Model3", "Model4", "Model5", "Model6"];
    const setColor = (color) => {
        dispatch(selectColor(color))
    }

    return (
        <div className="col-5 bg-light position-relative">
            <h2 className="my-3 mx-3">Außenlackierung</h2>
            <div>
                {
                    colors.map(color => <ColorItem color={color} setColor={setColor} selected={selectedColor===color}/>  
                    )
                }
            </div>
            <div className="text-center mt-3" style={{"border": "solid 2px green"}}>
                Ausgewählte Farbe samt Preis
            </div>
            <div className="bottom justify-content bg-light">
                <div className="d-inline-block col-6"> 2/6 Außenlackierung</div>
                <div className="d-inline-block col-6 text-end"><NavLink to="/interiorConfig"><button className="config-btn">Interieur <i className="bi bi-arrow-right" /></button></NavLink></div>
            </div>
        </div>
    )

}
export default ColorSelection;
