import idbuzzversion from '../img/idbuzz.webp';
import '../css/additional.css';
import { NavLink } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { getNewConfig } from '../actions/FetchAction';
import { selectModel } from '../actions/SelectAction';

//hier wird eine modelvariante übergeben
const ModelVersion = ({ model, configId }) => {
    const dispatch = useDispatch();

    const newConfig = () => {
        dispatch(getNewConfig(configId));
    }

    const setModel = (option) => {
        dispatch(selectModel(option))
    }

    return (

        <div className="w-25 position-relative" style={{"height": "100%"}}>
            <img className="img-fluid mb-3" src={idbuzzversion} alt="Modelbild" />

            <div style={{"height": "10%"}}>
                <p className="mb-0">
                    <strong>{model.title}</strong>
                </p>
                <p>
                    {model.description}
                </p>
            </div>


            <div className="overflow-auto" style={{"height": "35%"}}>
                <p className="text-justify pe-3" >{model.details}</p>
            </div>

            <p>
                <strong>Ab {model.priceInCents / 100} € inkl. MwSt. </strong>
            </p>

            <NavLink to="/engineConfig" style={{ "textDecoration": "none" }} onClick={() => newConfig()}><button className="config-btn" onClick={() => setModel(model)}>Konfigurieren</button></NavLink>


        </div>


    )


    /*<div className="w-25 vh-100">
           <img className="img-fluid mb-3" src={idbuzzversion} alt="Modelbild" />
           <p className="text-justify">Strom Kraftstoffverbrauch kombiniert
               18,9 kWh/100 km 1 •
               Strom CO₂ Emissionen kombiniert
               0 g/km 1 •
               Effizienzklasse
               A+++ 1</p>
           <p style={{ "marginBottom": "0" }}>
               <strong>PRO</strong>
           </p>
           <p>
               Das neue Maß an Raumgefühl
           </p>
           <p style={{ "marginBottom": "0" }}>
               <strong>Ab 64.581,30 € inkl. MwSt. 2</strong>
           </p>
           <p>
               Ab 54.270,00 € exkl. MwSt. 3
           </p>
           <NavLink to="/engineConfig" style={{ "textDecoration": "none" }}><button onClick={() => newConfig()} className="config-btn">Konfigurieren</button></NavLink>


       </div>*/

}

export default ModelVersion;
