import { useSelector } from "react-redux"



export const getExclusionsFromPreviousSteps = (step) => {
    const configuration = useSelector(state => state.config);
    let exclusionList;
    for (i = 1; i < step; i++) {
        exclusionList = [...exclusionList, Object.values(configuration)[i].exclusions]
    }

    return exclusionList;
}

export const getCategories = () => {
    let categories = new Set();
    interiors.forEach(element => categories.add(element.category))
    return [...categories]
}
