import '../css/additional.css';
import { NavLink } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { getNewConfig } from '../actions/FetchAction';
import { selectModel } from '../actions/SelectAction';

//hier wird eine modelvariante übergeben
const ModelVersion = ({ model, configId }) => {
    const dispatch = useDispatch();

    const newConfig = () => {
        dispatch(getNewConfig(configId));
    }

    const setModel = (option) => {
        dispatch(selectModel(option))
    }

    const imgUrl = "http://localhost:3000/" + model.imgUrl + ".webp"

    return (
        <div className="w-25 position-relative" style={{ "height": "100%" }}>
            <img className="img-fluid mb-3" src={imgUrl} alt="Modelbild" />
            <div style={{ "height": "10%" }}>
                <p className="mb-0">
                    <strong>{model.title}</strong>
                </p>
                <p>
                    {model.description}
                </p>
            </div>
            <div className="overflow-auto" style={{ "height": "35%" }}>
                <p className="text-justify pe-3" >{model.details}</p>
            </div>
            <p>
                <strong>Ab {model.priceInCents / 100} € inkl. MwSt. </strong>
            </p>
            <NavLink to="/engineConfig" style={{ "textDecoration": "none" }} onClick={() => newConfig()}><button className="config-btn" onClick={() => setModel(model)}>Konfigurieren</button></NavLink>
        </div>
    )
}

export default ModelVersion;
