import { useDispatch, useSelector } from "react-redux";
import { selectExtras } from "../actions/SelectAction";
import ExtraItem from "./ExtraItem";

const ExtraSelection = () => {

    const dispatch = useDispatch();
    const selectedExtra = useSelector(state => state.config.wheel);
    const groups = ["Group1", "Group2", "Group3"]
    const options = ["Model1", "Model2", "Model3", "Model4", "Model5", "Model6"];
    const setExtras = (option) => {
        dispatch(selectExtras(option))
    }



    return (
        <div className="col-5 bg-light position-relative overflow-auto ">
            <h2 className="my-3 mx-3">WÃ¤hlen Sie Ihr Interieur.</h2>
            <div className="accordion" id="accordionExample">
                <div className="accordion-item">
                    <h2 className="accordion-header" id="headingOne">
                        <button className="accordion-button" type="button" data-bs-toggle="collapse" data-bs-target="#collapseOne" aria-expanded="true" aria-controls="collapseOne">
                            Accordion Item #1
                        </button>
                    </h2>
                    <div id="collapseOne" className="accordion-collapse collapse show" aria-labelledby="headingOne" data-bs-parent="#accordionExample">
                        <div className="accordion-body">
                            <strong>This is the first item's accordion body.</strong> It is shown by default, until the collapse plugin adds the appropriate classes that we use to style each element. These classes control the overall appearance, as well as the showing and hiding via CSS transitions. You can modify any of this with custom CSS or overriding our default variables. It's also worth noting that just about any HTML can go within the <code>.accordion-body</code>, though the transition does limit overflow.
                        </div>
                    </div>
                </div>

            </div>
        </div>
    )

}

export default ExtraSelection;
