import ColorItem from "./ColorItem";
import '../../css/additional.css'
import { useSelector, useDispatch } from "react-redux";
import { selectColor } from "../../actions/SelectAction";
import { getAllColors } from "../../actions/FetchAction";
import { useEffect } from "react";


//bekommt Liste der zur Auswahl stehenden Farben
const ColorSelection = () => {

    const dispatch = useDispatch();
    const selectedColor = useSelector(state => state.config.color);
    const colors = useSelector(state => state.attributes.colors);
    const categories = ["Uni","Uni/Metallic (zweifarbig)","Metallic","Perleffekt"]
    var colorCategory;


    const setColor = (color) => {
        dispatch(selectColor(color))
    }

    return (
        <div className="col-5 bg-light">
            <h2 className="my-3 mx-3">Außenlackierung</h2>
            <div>


                {
                categories.map(category => { 
                   colorCategory = colors.filter(color => color.category === category)
                    return (
                        <div>
                        <small className="text-muted d-block mb-2">{category}</small>
                        {colorCategory.map((color,i) => <ColorItem key={i} color={color} setColor={setColor} selected={selectedColor===color}/>)}
                        </div>
                        )
                })
                }
            </div>
            <div className="text-center mt-1 mb-3" style={{"border": "solid 2px green"}}>
               {selectedColor.description === undefined ? "Keine Farbe ausgewählt" : selectedColor.description +" Preis inkl. MwSt. " + selectedColor.priceInCents/100 + " €" }
            </div>
        </div>
    )

}
export default ColorSelection;
