import '../css/button.css';
import '../css/bottom.css'
import EngineItem from './EngineItem';
import { NavLink } from 'react-router-dom';
import { selectEngine } from '../actions/SelectAction';
import { useDispatch, useSelector } from 'react-redux';

//hier werden die jeweiligen Konfigurationsmöglichkeiten übergeben (bspw. motorenliste)
const EngineSelection = () => {

    const dispatch = useDispatch();
    const selectedEngine = useSelector(state => state.config.engine);
    const engines = ["Model1", "Model2"];
    const setEngine = (engine) => {
        dispatch(selectEngine(engine))
    }

    return (
        <div className="col-5 bg-light position-relative overflow-auto vh-50" >
            <h2 className="my-3 mx-3">Motorenauswahl</h2>
            <div>
                {engines.map(engine => <EngineItem setEngine={setEngine} engine={engine} selected={engine === selectedEngine} />
                )}
            </div>
            <div className="bottom justify-content bg-light">
                <div className="d-inline-block col-6"> 1/6 Motoren</div>
                <div className="d-inline-block col-6 text-end"><NavLink to="/colorConfig"><button className="config-btn">Farben <i className="bi bi-arrow-right" /></button></NavLink></div>
            </div>
        </div>
    )
}

export default EngineSelection;
