const ExtraItem = ({ collapse, option, setExtra, selected }) => {
    console.log(option)
    return (

        <div onClick={() => setExtra(option)} id={collapse} className="accordion-collapse collapse hide" aria-labelledby="headingOne" data-bs-parent="#accordionExample" >
            <div className="accordion-body d-flex">
                <div className="col-11 d-flex">
                    <div className="col-6"><strong >{option.title} </strong></div>
                    <div className="col-4"><strong >{`${option.priceInCents / 100}€`} </strong></div>

                </div>
                <input className="form-check-input justify-content-end" type="radio" checked={selected} readOnly={true} />
            </div>
        </div>
    )
}

export default ExtraItem;
