import { NavLink } from "react-router-dom";
import '../css/additional.css'
import { useSelector } from "react-redux";

const Footer = ({ step, linkTo, next }) => {

    var calculatedPrice = 0;
    const currentConfig = useSelector(state => state.config)
    const attributes = [currentConfig.engine, currentConfig.color, currentConfig.interior, currentConfig.wheels, currentConfig.extras]
    { attributes.forEach(attribute => attribute !== undefined ? attribute.priceInCents ? calculatedPrice += attribute.priceInCents : " " : " ") }
    let formattedPrice = `${calculatedPrice / 100}`.slice(-1) == 0 ? `${calculatedPrice / 100}0€` : `${calculatedPrice / 100}€`



    return (
        <div className="d-flex ">
            <div className="align-bottom border-top col-7 px-1 py-1">
                <div className="col-6 d-inline-block">
                    <button className="btn btn-light "><i className="bi bi-download"></i> Konfiguration sichern</button>
                </div>
                <div className="col-6 d-inline-block">
                    <strong>Preis inkl. MwSt. {formattedPrice}</strong>
                </div>
            </div>
            <div className=" justify-content bg-vwblue col-5 px-1 py-1">
                <div className="d-inline-block col-6 text-white"> {step}</div>
                <div className="d-inline-block col-6 text-end"><NavLink to={linkTo}><button className="vw-btn">{next} <i className="bi bi-arrow-right" /></button></NavLink></div>
            </div>


        </div>


    )
}

export default Footer;
