export const SAVE_CONFIG = "SAVE_CONFIG";
export const GET_ATTRIBUTES = "GET_ATTRIBUTES"
export const NEW_CONFIG = "NEW_CONFIG"

const BASE_URL = "http://localhost:8080/api/"


export const getNewConfig = (configId) => async (dispatch) => {
  try {
    console.log({ configId })
    const response = await fetch(`${BASE_URL}configuration/${configId}`);
    const payload = await response.json();
    dispatch({
      type: NEW_CONFIG,
      action: payload
    })
  } catch (error) {
    console.log(error);
  }

}

export const postConfiguration = (configuration) => async (dispatch) => {
  let config = [];
  Object.values(configuration).map(attr => {
    if (Array.isArray(attr)) {
      attr.map(innerAttr => config = [...config, innerAttr])
    } else {
      config = [...config, attr]
    }
  })
  config = { carConfigAttributes: config.slice(0, config.length - 1) };

  try {
    const response = await fetch(
      BASE_URL + "configuration",
      {
        method: "POST",
        body: JSON.stringify(config),
        headers: {
          "Content-Type": "application/json",
          "Accept": "application/json"
        }
      }
    );
    const postObject = await response.json();
    dispatch({
      type: SAVE_CONFIG,
      payload: postObject
    });
  } catch (error) {
    console.log(error);
  }

}

export const getAllAttributes = () => async (dispatch) => {
  try {
    const response = await fetch(BASE_URL + "attributes");
    const payload = await response.json();
    dispatch({
      type: GET_ATTRIBUTES,
      payload: payload
    });
  } catch (error) {
    console.log(error);
  }
}
