import { SET_ENGINE, SET_COLOR, SET_EXTRAS, SET_INTERIOR, SET_WHEEL } from "../actions/SelectAction"
import { SAVE_CONFIG } from "../actions/FetchAction"

const currentState = JSON.parse(localStorage.getItem("currentConfig"));

let initialState = {
    engine: "",
    color: "",
    interior: "",
    wheel: "",
    extra: [],
    savedConfiguration: {},
};
let nextState;

const ConfigurationReducer = (state = initialState, action) => {
    switch (action.type) {
        case SET_ENGINE:
            nextState = { ...state, engine: action.payload }
            localStorage.setItem("currentConfig", JSON.stringify(nextState));
            return nextState;
        case SET_COLOR:
            nextState = { ...state, color: action.payload }
            localStorage.setItem("currentConfig", JSON.stringify(nextState));
            return nextState;
        case SET_EXTRAS:
            if (state.extra.includes(action.payload)) {
                nextState = { ...state, extra: [] };
                state.extra.forEach(extra => {
                    if (extra !== action.payload) {
                        nextState.extra = [...nextState.extra, extra]
                    }
                })
                localStorage.setItem("currentConfig", JSON.stringify(nextState));
                return nextState;
            }
            else {
                nextState = { ...state, extra: [...state.extra, action.payload] }
                localStorage.setItem("currentConfig", JSON.stringify(nextState));
                return nextState;
            }
        case SET_INTERIOR:
            nextState = { ...state, interior: action.payload };
            localStorage.setItem("currentConfig", JSON.stringify(nextState));
            return nextState;
        case SET_WHEEL:
            nextState = { ...state, wheel: action.payload };
            localStorage.setItem("currentConfig", JSON.stringify(nextState));
            return nextState;
        case SAVE_CONFIG:
            nextState = { ...state, savedConfiguration: action.payload };
            localStorage.setItem("currentConfig", JSON.stringify(nextState));
            return nextState;
        default:
            return state;
    }
}
export default ConfigurationReducer;
