import { useDispatch, useSelector } from "react-redux";
import { selectExtras } from "../actions/SelectAction";
import ExtraItem from "./ExtraItem";

const ExtraSelection = () => {

    const dispatch = useDispatch();
    const selectedExtra = useSelector(state => state.config.wheel);
    const options = ["Model1", "Model2", "Model3", "Model4", "Model5", "Model6"];
    const setExtras = (option) => {
        dispatch(selectExtras(option))
    }



    return (
        <div className="col-5 bg-light position-relative overflow-auto vh-5">
            <div className="">

                <button class="btn  dropdown-toggle" type="button" id="dropdownMenuButton" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                    EXTRA ÃœBERSCHRIFT
                </button>
                <div class="dropdown-menu" aria-labelledby="dropdownMenuButton">
                    {
                        options.map(option => <ExtraItem option={option} setExtras={setExtras} selected={selectedExtra === option} />
                        )
                    }
                </div>



            </div>
        </div>

    )

}

export default ExtraSelection;
