import { useDispatch, useSelector } from "react-redux";
import { selectExtras } from "../actions/SelectAction";
import ExtraItem from "./ExtraItem";

const ExtraSelection = () => {

    const dispatch = useDispatch();
    const selectedExtra = useSelector(state => state.config.wheel);
    const groups = ["Group1", "Group2", "Group3"]
    const options = ["Model1", "Model2", "Model3"];
    const setExtras = (option) => {
        dispatch(selectExtras(option))
    }



    return (
        <div className="col-5 bg-light position-relative overflow-auto ">
            <h2 className="my-3 mx-3">WÃ¤hlen Sie Ihr Extra.</h2>
            {
                groups.map(group => {
                    return (
                        <div className="accordion" id="accordionExample">
                            <div className="accordion-item">
                                <h2 className="accordion-header" id="headingOne">
                                    <button className="accordion-button" type="button" data-bs-toggle="collapse" data-bs-target="#collapseOne" aria-expanded="true" aria-controls="collapseOne">
                                        {group}
                                    </button>
                                </h2>
                                {
                                    options.map(option => <ExtraItem option={option} setExtras={setExtras} selected={selectedExtra === option} />
                                    )
                                }
                            </div>
                        </div>)

                })
            }

        </div>
    )

}

export default ExtraSelection;
