import { NavLink } from "react-router-dom";
import WheelItem from "./WheelItem";
import { useDispatch, useSelector } from "react-redux";
import { selectWheel } from "../actions/SelectAction";

const WheelSelection = () => {

    const dispatch = useDispatch();
    const selectedWheel = useSelector(state => state.config.wheel);
    const options = ["Model1", "Model2"];
    const setWheel = (option) => {
        dispatch(selectWheel(option))
    }


    return (
        <div className="col-5 bg-light position-relative">
            <h2 className="my-3 mx-3">Wählen Sie Ihre Räder / Reifen</h2>
            <div>
                <div>
                    <small className="text-muted">Räder / Reifen - 17" </small>
                    {
                        options.map(option => <WheelItem option={option} setWheel={setWheel} selected={selectedWheel === option} />
                        )
                    }
                </div>
                <div>
                    <small className="text-muted">Räder / Reifen - 18" </small>
                    {
                        options.map(option => <WheelItem option={option} setWheel={setWheel} selected={selectedWheel === option} />
                        )
                    }
                </div>
                <div>
                    <small className="text-muted">Räder / Reifen - 19" </small>

                </div>

            </div>
            <div className="text-center mt-3" style={{ "border": "solid 2px green" }}>
                Ausgewähltes Räder samt Preis
            </div>
            <div className="bottom justify-content bg-light">
                <div className="d-inline-block col-6 "> 4/6 Räder / Reifen</div>
                <div className="d-inline-block col-6 text-end"><NavLink to="/interiorConfig"><button className="config-btn">Sonderausstattung <i class="bi bi-arrow-right" /></button></NavLink></div>
            </div>
        </div>

    )



}

export default WheelSelection;
