import ColorItem from "./ColorItem";
import '../../css/additional.css'
import { useSelector, useDispatch } from "react-redux";
import { selectColor } from "../../actions/SelectAction";
import { getAllColors } from "../../actions/GetAttributesAction";
import { useEffect } from "react";


//bekommt Liste der zur Auswahl stehenden Farben
const ColorSelection = () => {

    const dispatch = useDispatch();
    const selectedColor = useSelector(state => state.config.color);
    const colors = useSelector(state => state.attributes.colors);
    const setColor = (color) => {
        dispatch(selectColor(color))
    }

    return (
        <div className="col-5 bg-light">
            <h2 className="my-3 mx-3">Außenlackierung</h2>
            <div>
                {
                    colors.map((color,i) => <ColorItem key={i} color={color} setColor={setColor} selected={selectedColor===color}/>  
                    )
                }
            </div>
            <div className="text-center mt-3" style={{"border": "solid 2px green"}}>
                Ausgewählte Farbe samt Preis
            </div>
        </div>
    )

}
export default ColorSelection;
