import { useSelector } from 'react-redux';
import idbuzzconfig from '../../img/IDBuzz-config-Mittel.jpeg';
import Header from '../Header';


const SummaryView = () => {

    const currentConfig = useSelector(state => state.config);
    const engine = currentConfig.engine;
    const color = currentConfig.color;
    const interior = currentConfig.interior;
    const wheels = currentConfig.wheels;
    const extras = currentConfig.extras;
    const germanDescription = ["Motor", "Außenfarbe", "Interieur", "Räder/Reifen", "Extras"]
    const attributes = [currentConfig.engine, currentConfig.color, currentConfig.interior, currentConfig.wheels, currentConfig.extras]

    return (
        <div>
            <Header />
            <div className="d-flex">
                <div className="col-5">
                    Zusammenfassung <strong>ID. Buzz</strong>
                    <p>ID. Buzz Pro KR 150 kW 77 kWh 1-Gang-Automatik; Strom; Kraftstoffverbrauch kombiniert: 18,9 kWh/100 km ⁠1 und CO₂-Emission kombiniert: 0 g/km ⁠1; Effizienzklasse: A+++
                        ⁠              2; Kann auswählbare Optionen enthalten
                    </p>
                    <button>save</button>
                </div>
                <div className="col-7 text-center"><img src={idbuzzconfig} /></div>
            </div>
            <div className="d-flex">

                <div className="col-7">
                    {germanDescription.map((step, i) =>
                        <div>
                            {`${i + 1}. ${step}`}

                        </div>)}




                </div>

                <div className="col-5">

                </div>

            </div>



        </div>


    )

}

export default SummaryView;
