import {SET_ENGINE, SET_COLOR, SET_EXTRAS, SET_INTERIOR, SET_WHEEL} from "../actions/SelectAction"

let initialState = {
    engine: "",
    color: "",
    extra: [],
    interior: "",
    wheel: "",
};

const ConfigurationReducer = (state = initialState, action) => {
    switch (action.type) {
        case SET_ENGINE:
            return {...state, engine: action.payload};
        case SET_COLOR:
            return {...state, color: action.payload};
        case SET_EXTRAS:
            return {...state, extra: action.payload};
        case SET_INTERIOR:
            return {...state, interior: action.payload};
        case SET_WHEEL:
            return {...state, wheel: action.payload};
        default:
            return state;
    }
}
export default ConfigurationReducer;
