import {SET_ENGINE, SET_COLOR, SET_EXTRAS, SET_INTERIOR, SET_WHEEL} from "../actions/SelectAction"

let initialState = {
    engine: "",
    color: "",
    extra: [],
    interior: "",
    wheel: "",
};

const ConfigurationReducer = (state = initialState, action) => {
    switch (action.type) {
        case SET_ENGINE:
            return {...state, engine: action.payload};
        case SET_COLOR:
            return {...state, color: action.payload};
        case SET_EXTRAS:
            if (state.extra.includes(action.payload)){
                let newState = {...state, extra: []};
                state.extra.forEach( extra => {
                    if (extra !== action.payload){
                        newState.extra = [...newState.extra, extra]
                    }
                })
                return newState;
                }
                else {
                    return {...state, extra: [...state.extra, action.payload]}
                }
        case SET_INTERIOR:
            return {...state, interior: action.payload};
        case SET_WHEEL:
            return {...state, wheel: action.payload};
        default:
            return state;
    }
}
export default ConfigurationReducer;
